<?phpclass Model_ss_region extends Model_basemodel{	//@override	public function setPrimaryKey() {		$this->_primaryKey = 'region_id';	}	//@override	public function setTableName() {		$this->_tableName = 'ss_region';	}	public function getRegion($rid){		$returnData['data'] = array();        //获取条件 $where        if (!$rid) {            $condition=array(1);        }else{            $condition['parent_id'] = $rid;        }        $list = $this->getList($condition,'*','order by region_id asc');        foreach ($list as $v){            $returnData['data'][$v['region_id']] = $v['region_name'];        }		return $returnData['data'];	}		function get_area_condition($id=0,$regionType=1,$isIP=1){		if($isIP){			$ip = SUtil::getIp($isIP);			$city = SUtil::ip2city($ip);			$city = $city['city'];			$condition = "city_id in(0)";			if($city){				$row = $this->getOne(array("region_name like '{$city}%'",'region_id'));				if($row['region_id']){					$condition = "city_id in(0,{$row['region_id']})";				}			}		}else{			$strname = '';						switch($regionType){				case 1:					$strname = 'pro_id';					break;				case 2:					$strname = 'city_id';					break;				case 3:					$strname = 'area_id';					break;				default :					break;			}			if($strname&&$id){				$condition = "$strname in(0,{$id})";			}else{				$condition = "pro_id in(0)";			}		}		return $condition;	}	}/**	End file,Don't add ?> after this.*/